        public override void OnDraw()
        {
            if (levelClear[currentLevel] == true)
            {
                UnLoadCurrentLevel();
                currentLevel++;
                LoadNextLevel(levelMaps.ElementAt(currentLevel), PlainsArtRefs, PlainsArtRefsTags);
            }
            if (up)
            {
                if (up == true && left == true)
                {
                    up = false;
                }
                if (up == true && right == true)
                {
                    up = false;
                }
                Log.Warning("Up has no animation currently.");
            }
            if (down)
            {
                if (down == true && left == true)
                {
                    down = false;
                }
                if (down == true && right == true)
                {
                    down = false;
                }
                if (facedirection == 0)
                {
                    AnimatePlayer(19, 19);
                }
                else if (facedirection == 1)
                {
                    AnimatePlayer(8, 8);
                }
            }
            if (left)
            {
                facedirection = 0;
                AnimatePlayer(12, 17);
            }
            if (right)
            {
                facedirection = 1;
                AnimatePlayer(1, 6);
            }
            if (jump)
            {
                if (facedirection == 0)
                {
                    AnimatePlayer(21, 21);
                }
                else if (facedirection == 1)
                {
                    AnimatePlayer(10, 10);
                }
            }
            if (jump == false && jumpmode == true)
            {
                if (facedirection == 0)
                {
                    AnimatePlayer(20, 20);
                }
                else if (facedirection == 1)
                {
                    AnimatePlayer(9, 9);
                }
            }
            if (StillInput(nokey))
            {
                if (facedirection == 0)
                {
                    AnimatePlayer(11, 11);
                }
                else if (facedirection == 1)
                {
                    AnimatePlayer(0, 0);
                }
            }
            //<- repair animating of player, to be separate from enemies, currently, animating player only works if there are no enemies in the level.

            UpdatePlayerCamera();

            //Log.Info($"current level length {currentLevelEndSize}");
            //Log.Info($"camera position: {CameraPosition.X}");
            //Log.Info($"player position: {player.Position.X}");
        }

        private void UpdatePlayerCamera()
        {
            if (player.Position.X <= 160)
            {
                CameraPosition.X = 0;
            }
            else if (player.Position.X > 160 && player.Position.X <= currentLevelEndSize - 160)
            {
                int diff = (int)player.Position.X - 160;
                CameraPosition.X = -diff;
            }
            else if (player.Position.X > currentLevelEndSize - 160)
            {
                if (currentLevelEndSize == 320)
                {
                    CameraPosition.X = currentLevelEndSize - 320;
                }
                else
                {
                    CameraPosition.X = -(currentLevelEndSize - 320);
                }
            }
        }


