        internal static string[,] ReturnLayer(string levelname, int layernumber)
        {
            if (levelname == "titlescreen")
            { 
                return titlescreen.ElementAt(layernumber);
            }
            if (levelname == "levelselect")
            {
                return levelselect.ElementAt(layernumber);
            }
            if (levelname == "worldmap")
            {
                return worldmap.ElementAt(layernumber);
            }
            if (levelname == "leveloneone")
            {
                return leveloneone.ElementAt(layernumber);
            }
            return null;
        }
    }
